import React, { useState, useEffect } from "react";
import { useParams,useHistory } from "react-router-dom";
import axios from "axios";

function Card({ item }) {
  let { id } = useParams();

  const [card, setCard] = useState(null);
  const history = useHistory()






  const getData = async () => {
    const response = await axios.get(
      "https://api.pokemontcg.io/v2/cards?pageSize=10"
    );



    setCard(
      response.data.data.find((ele) => {
        return ele.id == id;
      })
    );
  };

  useEffect(() => {
    getData();
  }, []);





  // const addLike = (id) => {
  //   const newName = true;
  //   const newarr = pokemon.map(item => {
  //       if(id === item.id && item.isLiked === false){
  //           return {...item, isLiked: newName}
  //       } else{
  //           return item;
  //       }
  //   });
  //   setPokemon(newarr);
  //   console.log(newarr);
  //   localStorage.setItem("arr",JSON.stringify(newarr))

  // };

  const handleClick = () => {
    history.push(`/card/${id}`)
  }



  return (
    <div className="card" onClick={() => handleClick(item.id)}>
      {card ? <h3> {card?.name}</h3> : <h3> {item?.name}</h3>}
      <img src={card ? card?.images.small : item?.images.small} alt="img" />
      {/* <button onClick={() => addLike(item.id)}>Like</button> */}
    </div>
  );
}

export default Card;
