import React, { useState, useEffect } from 'react'
import { useParams } from "react-router-dom";
import axios from 'axios';


function Card({ item }) {

  let { id } = useParams();

  const [pokemon, setPokemon] = useState([]);

  useEffect(() => {
    getData();
  }, [])

  const getData = async () => {
    const response = await axios.get("https://api.pokemontcg.io/v2/cards?pageSize=10");
    setPokemon(response.data.data)
  }

  let card = pokemon.find(ele => ele.id === id*1)

  const addLike = (id) => {
    const newName = true;
    const newarr = pokemon.map(item => {
        if(id === item.id && item.isLiked === false){
            return {...item, isLiked: newName}
        } else{
            return item;
        }
    });
    setPokemon(newarr);
    console.log(newarr);
    localStorage.setItem("arr",JSON.stringify(newarr))

  };


  return (
    <div className="card">
      <h3>{card ? card.name : item.name}</h3>
      <img src={card ? card.images.small : item.images} alt="img"/>
      <button onClick={() => addLike(item.id)}>Like</button>
    </div>
  );
}

export default Card;
